import React, { useState } from "react";

function Card() {
  const [color, changeColor] = useState("");
  function handleClick(e) {
    console.log(e.target.getPropertyValue("border-radius"));

    changeColor("Red");
  }

  return (
    <div className="card">
      <div className="card__outer-container">
        <h1 className="card__header">{color} Color Chooser</h1>
        <div className="card__inner-container">
          <div className="card__color-swatch" onClick={handleClick}></div>
        </div>
      </div>
    </div>
  );
}

export default Card;
