import React, { useState } from "react";
import Colors from "../colors.json";

function Card() {
  const [color, setColor] = useState("");
  const [toggle, setToggle] = useState(false);
  const rand = Math.floor(Math.random() * 7);

  function handleClick(e) {
    const random_hex_color_code = (Math.random() * 0xfffff * 1000000).toString(16);
    const final_color = "#" + random_hex_color_code.slice(0, 6);
  }

  return (
    <div className="card">
      <div className="card__outer-container">
        <h1 className="card__header">Color Chooser</h1>
        <div className="card__inner-container">
          <div
            className="card__color-swatch"
            onClick={handleClick}
            style={{ backgroundColor: final_color }}
          ></div>
          <p>{final_color}</p>
        </div>
      </div>
    </div>
  );
}

export default Card;
