import React, { useState } from "react";
import Colors from "../colors.json";

function Card() {
  const [color, changeColor] = useState("");
  const [toggle, setToggle] = useState(false);
  const rand = Math.floor(Math.random() * 7);

  function handleClick(e) {
    setToggle(toggle ? null : true);
  }

  return (
    <div className="card">
      <div className="card__outer-container">
        <h1 className="card__header">{Colors.colors[rand]} Color Chooser</h1>
        <div className="card__inner-container">
          <div
            className="card__color-swatch"
            onClick={handleClick}
            style={toggle ? { backgroundColor: Colors.colors[rand] } : { backgroundColor: "red" }}
          ></div>
        </div>
      </div>
    </div>
  );
}

export default Card;
