import React, { useState } from "react";
import Colors from "../colors.json";

function Card() {
  const [color, setColor] = useState("");
  const [toggle, setToggle] = useState(false);
  const rand = Math.floor(Math.random() * 7);

  function handleClick(e) {
    setToggle(toggle ? null : true);
    setColor(Colors.colors[rand]);
  }

  const random_hex_color_code = () => {
    let n = (Math.random() * 0xfffff * 1000000).toString(16);
    return "#" + n.slice(0, 6);
  };

  return (
    <div className="card">
      <div className="card__outer-container">
        <h1 className="card__header">Color Chooser</h1>
        <div className="card__inner-container">
          <div
            className="card__color-swatch"
            onClick={handleClick}
            style={toggle ? { backgroundColor: random_hex_color_code } : { backgroundColor: color }}
          ></div>
        </div>
      </div>
    </div>
  );
}

export default Card;
